import React, { useState } from 'react';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [redactedText, setRedactedText] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setError(null);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      setError('Please select a PDF file');
      return;
    }

    setLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:8000/api/redact-pdf', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.error) {
        setError(data.error);
      } else {
        setRedactedText(data.redacted_text);
      }
    } catch (err) {
      setError('Failed to process the PDF');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>PDF Redaction Tool</h1>
      </header>

      <main>
        <form onSubmit={handleSubmit}>
          <div className="file-input">
            <input
              type="file"
              accept=".pdf"
              onChange={handleFileChange}
            />
          </div>
          <button type="submit" disabled={!file || loading}>
            {loading ? 'Processing...' : 'Redact PDF'}
          </button>
        </form>

        {error && <div className="error">{error}</div>}

        {redactedText && (
          <div className="results">
            <h2>Redacted Text:</h2>
            {redactedText.map((page, index) => (
              <div key={index} className="page">
                <h3>Page {index + 1}</h3>
                <pre>{page}</pre>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
